package programs;

public class EvenorOddClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
           int n=3;
           if(n%2==0) {
        	   System.out.println(n + " is Even");
           }else {
        	   System.out.println(n + " is Odd");
           }
	}

}
