package programs;

public class LeapClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int n=2022;
         if(n%4==0) {
        	 System.out.println(n + " is leap year.");
         }else {
        	 System.out.println(n + " is not leap year.");
         }
	}

}
